mapper.select:
select category_id as categoryId,name as
c_name,
age as c_age,
salary as
c_salary,
status as c_status,
description as c_describe
from category


		name like #{name}


		AND age = #{age}


		AND status like #{status}



	ORDER BY ${column} ${order.name()}

